// ============================================
// Extract User Info from Supabase
// Purpose: Prepare user data for message routing
// ============================================

const user = $input.first().json || {};
// Mengambil data pesan dari node "Code in JavaScript1"
const messageData = $('Code in JavaScript1').first().json || {};

// ============================================
// CHECK USER EXISTENCE
// ============================================

const userExists = !!user.id;

if (!userExists) {
  return [{
    json: {
      user_exists: false,
      error: "User not found in database",
      // Menggunakan phone_normalized dari input JavaScript1
      phone_searched: messageData.phone_normalized 
    }
  }];
}

// ============================================
// EXTRACT CORE USER DATA
// ============================================

// Mengambil nickname dari full_name (misal: "Farmer Lyori (Pak Tani)")
let nickname = "";
if (user.full_name) {
  // Cek jika ada nama dalam kurung
  const parenthesesMatch = user.full_name.match(/\(([^)]+)\)/);
  if (parenthesesMatch) {
    nickname = parenthesesMatch[1]; // Hasil: "Pak Tani"
  } else {
    // Jika tidak ada kurung, ambil kata pertama
    const words = user.full_name.trim().split(/\s+/);
    if (words.length > 0) {
      nickname = words[0];
    }
  }
}

// ============================================
// ROLE-BASED ROUTING
// ============================================

// Menentukan jalur routing berdasarkan role
const routingDecision = (() => {
  switch(user.role) {
    case 'farmer':
    case 'employee':
      return 'DAILY_REPORT_INPUT';

    case 'manager':
      return 'DAILY_REPORT_OR_QUERY';

    case 'ceo':
      return 'DOCUMENT_UPLOAD_OR_QUERY';

    case 'owner':
      return 'RAG_QUERY_ONLY';

    default:
      return 'UNKNOWN_ROLE';
  }
})();

// ============================================
// PERMISSIONS CHECK
// ============================================

const permissions = {
  can_submit_daily_report: ['farmer', 'employee', 'manager'].includes(user.role),
  can_upload_document: ['ceo', 'manager', 'owner'].includes(user.role),
  can_query_rag: ['manager', 'ceo', 'owner'].includes(user.role),
  can_view_all_companies: user.role === 'owner',
  can_approve_reports: ['manager', 'ceo', 'owner'].includes(user.role)
};

// ============================================
// RETURN NORMALIZED USER DATA
// ============================================

return [{
  json: {
    // User existence
    user_exists: true,

    // Core fields
    user_id: user.id,
    phone_number: user.phone_number,
    full_name: user.full_name,
    nickname: nickname,
    role: user.role,
    company_id: user.company_id,
    is_active: user.is_active,

    // Routing
    routing_decision: routingDecision,

    // Permissions
    permissions: permissions,

    // Message metadata (dari node Code in JavaScript1)
    message_body: messageData.chatInput, // "halo"
    message_id: messageData.id || "N/A", 
    timestamp: messageData.timestamp || new Date().toISOString(),

    // For database insert
    direction: 'inbound',

    // Debug
    _source: 'user_info_extractor'
  }
}];

// 1. Deklarasi payload utama dari _data
const data = $input.first().json.payload?._data || {};

// 2. Akses info pengirim dari path baru yang kamu berikan
const senderAlt = data.Info?.SenderAlt || {};

// 3. Ambil remoteJid (Bisa dari JID langsung atau properti id di SenderAlt)
const rawJid = 
  (typeof senderAlt === 'string' ? senderAlt : senderAlt.id) || 
  data.key?.remoteJid || 
  null;

if (!rawJid) {
  return [{
    json: {
      isValid: false,
      reason: "remoteJid tidak ditemukan di SenderAlt maupun key",
      debug_info: { senderAlt }
    }
  }];
}

// 4. Bersihkan & Normalisasi Nomor ke 08xxxx (Ganti 62 ke 0)
let phone = rawJid.split(':')[0].replace(/\D/g, '');

if (phone.startsWith('62')) {
  phone = '62' + phone.slice(2); // Menghasilkan 08xxxx
} else if (!phone.startsWith('0')) {
  phone = '62' + phone; // Memastikan diawali 0 jika pengirim nomor luar/asing
}

// 5. Ambil teks pesan (Conversation atau ExtendedText)
const msg = data.message || {};
const chatInput = ($input.first().json.payload._data.RawMessage.conversation);

return [{
  json: {
    isValid: true,
    phone_normalized: phone,
    pushName: senderAlt.name || senderAlt.pushName || data.pushName || "User",
    chatInput: chatInput,
    rawJid: rawJid
  }
}];

