// Ambil string JSON dari node sebelumnya
const rawText = $input.first().json.output;

// Parse string JSON
let data;
try {
  data = JSON.parse(rawText);
} catch (e) {
  throw new Error('Invalid JSON string from AI output');
}

// Format current date: "21 March 2026"
const currentDate = new Date().toLocaleDateString('en-GB', {
  day: '2-digit',
  month: 'long',
  year: 'numeric',
});

// FORCE schema: field wajib ada walaupun tidak dikirim AI
const result = {
  activities: {
    summary: data.activities?.summary ?? null,
    details: data.activities?.details ?? null,
  },

  issues: Array.isArray(data.issues)
    ? data.issues.map(issue => ({
        type: issue.type ?? null,
        severity: issue.severity ?? null,
        notes: issue.notes ?? null,
      }))
    : [],

  weather: data.weather ?? null,
  notes: data.notes ?? null,
  currentdate: currentDate,
};

// Return final output
return [
  {
    json: result,
  },
];

